mapper.select:
SELECT  COUNT(*)
FROM AUTH 
WHERE ADMIN_AUTH = #{adminAuth}

SELECT * FROM ( SELECT rownum rnum, B.* FROM (
SELECT ex_no , ex_regdate , ex_title , ex_address , ex_content , ex_startDay , ex_endDay , ex_reserveStartDay , ex_reserveEndDay , ex_enterStartTime , ex_enterEndTime , ex_price , ex_activate , admin_id FROM Exhibition WHERE ex_title LIKE '%' || #{search} || '%' ORDER BY ex_no DESC
) B ) EXHIBITION WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:

UPDATE Exhibition
SET ex_no = #{exNo}
	, ex_title = #{exTitle}
	, ex_address = #{exAddress}
	, ex_content = #{exContent}
	, ex_startDay = to_date(#{exStartDayString}, 'YYYY-MM-DD')
	, ex_endDay = to_date(#{exEndDayString}, 'YYYY-MM-DD')
	, ex_reserveStartDay = to_date(#{exReserveStartDayString}, 'YYYY-MM-DD')
	, ex_reserveEndDay = to_date(#{exReserveEndDayString}, 'YYYY-MM-DD')
	, ex_enterStartTime = to_date(#{exEnterStartTimeString}, 'HH24:MI:SS')
	, ex_enterEndTime = to_date(#{exEnterEndTimeString}, 'HH24:MI:SS')
	, ex_price = #{exPrice}
	, admin_id = #{adminId}
WHERE ex_no = #{exNo}

mapper.insert:
INSERT INTO ex_img ( 
	ex_img_no
