mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM (
SELECT con_no , con_regdate , con_title , con_address , con_content , con_startday , con_endday , con_reserveStartdayVIP , con_reserveStartday , con_reserveEndday , con_activate , admin_id FROM Concert WHERE con_title LIKE '%' || #{search} || '%' ORDER BY con_no DESC
) B ) CONCERT WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
