mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM (
SELECT con_round_no , con_round , con_round_starttime , con_round_endtime , con_no FROM CON_ROUND WHERE con_no = #{conNo} ORDER BY con_no DESC
) B ) CON_ROUND WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.insert:

	SELECT con_round_no_seq.nextval FROM dual


INSERT INTO CON_ROUND ( 
	con_round_no
	, con_round
	, con_round_starttime
	, con_round_endtime
	, con_no
)
VALUES ( 
	#{conRoundNo}
	, #{conRound}
	, to_date(#{conRoundStartTimeString}, 'HH24:MI:SS')
	, to_date(#{conRoundEndTimeString}, 'HH24:MI:SS')
	, #{conNo}
)

mapper.update:
UPDATE CON_ROUND
SET con_round_no = #{conRoundNo}
	, con_round = #{conRound}
	, con_round_starttime = to_date(#{conRoundStartTimeString}, 'HH24:MI:SS')
	, con_round_endtime = to_date(#{conRoundEndTimeString}, 'HH24:MI:SS')
	, con_no = #{conNo}
WHERE con_round_no = #{conRoundNo}

mapper.select:
SELECT * FROM ( SELECT rownum rnum, B.* FROM (
SELECT seat_seq , seat_level , seat_price , seat_no , con_round_no FROM SEAT WHERE con_round_no = #{conRoundNo} ORDER BY seat_no DESC
) B ) SEAT WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.insert:

	SELECT seat_seq.nextval FROM dual


INSERT INTO SEAT ( 
	seat_seq
	, seat_level
	, seat_price
	, seat_no
	, con_round_no
)
VALUES ( 
	#{seatSeq}
	, #{seatLevel}
	, #{seatPrice}
	, #{seatNo}
	, #{conRoundNo}
)

mapper.update:
UPDATE SEAT
SET seat_seq = #{seatSeq}
	, seat_level = #{seatLevel}
	, seat_price = #{seatPrice}
	, seat_no = #{seatNo}
	, con_round_no = #{conRoundNo}
WHERE seat_seq = #{seatSeq}
